(function($,window,document){$.fn.stickyelement=function(offsetBottom){var element=$(this);var w_height=$(window).height();var d_height=$(document).height();var e_height=element.height();var stop=0;var offsetTop=element.offset().top;$(window).scroll(function(){var top,bottom;var scroll=$(window).scrollTop();if(e_height<w_height){stop=offsetTop;top='0px';bottom='auto';}else{stop=e_height-w_height+offsetTop;top='auto';bottom='0px';}var j=scroll+w_height;var i=d_height-offsetBottom;if(j>(i-offsetBottom)){bottom=(j-i+offsetBottom)+'px';}if(scroll>stop){element.css({position:'fixed',top:top,bottom:bottom});}else{element.css({position:'static',top:'auto',bottom:'auto'});}});};})(jQuery, window, document);
